import { Injectable, Inject } from "@angular/core";
import { HttpClient, HttpHeaders } from "@angular/common/http";
import { CookieService } from "ngx-cookie-service";
import { url_api } from "src/app/app-constants";
import { Observable, BehaviorSubject } from "rxjs";
import { LoginInterface } from "src/components/login/login.class";
import { Router } from "@angular/router";
import { User } from "src/models/User.model";

const httpOptions = {
  headers: new HttpHeaders({ 'Content-Type': 'application/json'})
};

@Injectable({
    providedIn: 'root'
  })
  export class LoginService {

    constructor(private http: HttpClient, 
                  private cookies: CookieService,
                  private router: Router
                ) {}

  async LoginUsers(loginInterface: LoginInterface): Promise<Observable<any>>{

    console.log(loginInterface);

    return await this.http.post<any>(`${url_api}/api/Users`, loginInterface, httpOptions)
    }

    async logout(): Promise<Observable<any>> {
      return await this.http.post<any>(`/api/Users`, httpOptions);
    }
  }
