import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators, FormBuilder } from '@angular/forms';
import { User } from 'src/models/User.modes';
import { LoginService } from 'src/services/login.service';
import { Role } from '../../models/rol.eModel';


@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css'],
  providers: [LoginService],
})

export class RegisterComponent implements OnInit  {

  checkoutForm: any;

  user: User = new User();

  constructor (
    private fb: FormBuilder,
    private loginService: LoginService,
  ) { 
    this.checkoutForm = fb.group({
      userName: "",
      Role: "",
      email: "",
      Password: "",

    })
  };

  public registerForm: FormGroup = new FormGroup({});
  public event: { preventDefault: () => void; } | undefined;

  Roles = ['Select', 'Public', 'Writer', 'Editor'];



  async ngOnInit() {
      await this.initForm()
  }
    async initForm() { this.registerForm = new FormGroup({
      userName: new FormControl('', Validators.required),
      role: new FormControl('', Validators.required),
      email: new FormControl('', Validators.required),
      password: new FormControl('', Validators.required),
    });
  }

  onSubmit() {
    // Process checkout data here
    this.user.UserName = this.checkoutForm.value.userName;
    this.user.Role = this.checkoutForm.value.Role;
    this.user.Email = this.checkoutForm.value.email;
    this.user.Password = this.checkoutForm.value.Password;
    console.log(this.checkoutForm.value);



    this.checkoutForm.reset();


  }
}
