import { Injectable } from "@angular/core";
import { HttpClient } from "@angular/common/http";
import { Observable } from "rxjs";
import { CookieService } from "ngx-cookie-service";
import { UserInterface } from '../components/register/register.class';
import { url_api } from "src/app/app-constants";

@Injectable({
    providedIn: 'root'
  })
  export class LoginService {
    constructor(private http: HttpClient, private cookies: CookieService) {}

  async login(id: Number, data: UserInterface) {
    return await this.http.put(`${url_api}/api/Users${id}`, data).subscribe();
    
  }
}
