import { Component, OnInit } from '@angular/core';
import { Post } from 'src/models/post.model';
import { HomeService } from 'src/services/home.service';
import { IPost } from 'src/components/post/post.class';
import { ActivatedRoute } from '@angular/router';
import { FormBuilder, FormGroup } from '@angular/forms';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  providers: [HomeService]
})
export class HomeComponent implements OnInit{

  posts: any
  postsForm: any
  editmode: boolean = false;
  postsId: string = "";
  formGroup!: FormGroup;
  checkoutForm: any;

  constructor(
    private fb: FormBuilder,
    private homeService: HomeService,

   ) { }

  async ngOnInit() {

    this.getData();
  }

  public async getData(){
    (await this.homeService.getPost()).subscribe((resp: any) => {
      this.posts = resp;
     });
    }

    public async deleteDataById(id: string) {
      console.log(id);

      (await this.homeService.DeletePost(id));{
        () => {
          this.getData();
          alert("Delete Succesfull");
          (error: any) => console.error(error);
          alert("Delete Failed");
        }
      }
    }
  }


<div class="container">
    <h2>Welcome to Blog Engine</h2>
    <br />
    <h3>Posts</h3> 
    <button class="btn btn-primary" routerLink="post-add">Add Post</button>
    <p *ngIf="!(posts && posts.length)">
        There are no posts to display
    </p>
    <table class="table" *ngIf="(posts && posts.length)">
        <thead>
                <th>Title</th>
                <th>Content</th>
                <th>CreatedAt</th>
                <th>Author</th>
                <th>Status</th>
                <th>Actions</th>
        </thead>
        <tbody>
           <!--Revisar porque no se llenan los datos--> 
            <tr *ngFor="let item of posts">
                <td>{{item.title}}</td>
                <td>{{item.content}}</td>
                <td>{{item.createdAt}}</td>
                <td>{{item.author}}</td>
                <td>{{item.status}}</td>
                <td>
                    <button class="btn btn-warning"  [routerLink]='["/home/post-edit/" + item.id]' >Edit</button>
                    <button class="btn btn-danger">Delete</button>
                </td>
            </tr>
        </tbody>
    </table>
</div>
