import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Post } from 'src/models/post.model';
import { IPost } from './post.class';
import { PostService } from '../../services/post.service';

@Component({
  selector: 'app-post',
  templateUrl: './post.component.html',
  styleUrls: ['./post.component.css'],
  providers:[PostService]
})
export class PostComponent implements OnInit {

  checkoutForm: any;
  post: Post = new Post();
  router: any

 constructor(
  private fb: FormBuilder,
  private postService: PostService,
  ) {
      this.checkoutForm = this.fb.group({
        title: "",
        content: "",
        createAt: "",
        status: "",
        author: "",
        comment: []

       });
   }

 formGroup: FormGroup | undefined;

 ngOnInit(){ }
  public postForm: FormGroup = new FormGroup({});
  public event: {preventDefault:() => void;} |undefined;


  onSubmit(){
    //Process fillout data here
    this.post.Title = this.checkoutForm.value.title;
    this.post.Content = this.checkoutForm.value.content;
    this.post.CreateAt = this.checkoutForm.value.createAt;
    this.post.Status = this.checkoutForm.value.status;
    this.post.Author = this.checkoutForm.value.autor;
    this.post.Comment = this.checkoutForm.value.comment;

    const data = this.checkoutForm.value;

    //prueba enviando data
    //console.log(data);

     try{
       this.postService.SendPost(data);

       alert("Registration Successful");
     }
    catch (error) {
      console.log(error);
      alert("Registration Failed");
    }
    this.checkoutForm.reset();
   }

}

<div class="container">
    <form [formGroup]="checkoutForm" class="form-post" novalidate (ngSubmit)="onSubmit()" method="post">
        <fieldset>
            <div class="tilte form-group"
                  [ngClass]="{'has-error' : (checkoutForm.get('title').touched || checkoutForm.get('title').dirty) && !checkoutForm.get('title').valid}">
                <label for="title" class="sr-only">Title</label>
                <input name="title" type="text" id="title" class="form-control" placeholder="User Name" formControlName="title" 
                    required autofocus>
                <span class="help-block" *ngIf="(checkoutForm.get('title').touched || checkoutForm.get('title').dirty) && checkoutForm.get('title').errors">
                    <span *ngIf="checkoutForm.get('title').errors.required" class="danger">
                        Field tilte is required
                    </span> 
                </span>
            </div>

            <div class="content form-group"
                [ngClass]="{'has-error' : (checkoutForm.get('content').touched || checkoutForm.get('content').dirty) && !checkoutForm.get('content').valid}">
                <label for="content" class="sr-only">Content</label>
                <textarea name="content" id="content" class="form-control" placeholder="Write here your post"  formControlName="content" required >
                <span class="help-block" *ngIf="(checkoutForm.get('content').touched || checkoutForm.get('content').dirty) && checkoutForm.get('content').errors">
                    <span *ngIf="checkoutForm.get('content').errors.required" class="danger">
                        Field content is required
                    </span> 
                </span>
            </textarea>
            </div>

                <label for="content" class="sr-only">Date</label>
                <input id="Creation-Date" type="date" class="form-control" />
                <label for="author" class="sr-only">Author</label>

                <div class="form-group">
                    <div class="col-md-10 col-md-offset-2">
                        <span class="btn-post">
                            <button class="btn btn-primary" type="submit"[disabled]="!checkoutForm.valid">
                                Save
                            </button>

                            <button class="btn btn-default" type="button" [routerLink]='["/home"]'>
                                Cancel
                            </button>
                        </span>
                    </div>
                </div>
        </fieldset>
    </form>
</div>
